describe("Login test", () => {
  it("Successful login to the quote form", (done) => {
    const body = {
      username: "user1",
      password: "pass1",
    };
    chai
      .request(server)
      .post("/login")
      .send(body)
      .end((err, res) => {
        expect(res).have.status(200);
        expect(res.body).to.eql({ success: "quote.html" });
