import express from 'express'
import createError from 'http-errors'

interface ErrorWithStatus extends Error {
  status: number
  message: string
}
const Error404Middleware = (req: express.Request, res: express.Response, next: express.NextFunction) => {
  next(new createError.NotFound('This path does not exist'))
}
const ErrorHandlerMiddleware = (
  err: ErrorWithStatus,
  req: express.Request,
  res: express.Response,
  next: express.NextFunction
) => {
  res.status(err.status || 500)
  res.send({
    error: {
      status: err.status || 500,
      message: err.message
    }
  })
}

export default {
  Error404: Error404Middleware,
  ErrorHandlder: ErrorHandlerMiddleware
}

import express from "express";
import createError from "http-errors";
import { AuthSchema } from "../config/Validation/auth";
import MySQL from "../config/Init/initTypeMySQL";

export default {
  login: async (
    req: express.Request,
    res: express.Response,
    next: express.NextFunction
  ) => {
    try {
      await AuthSchema.validateAsync(req.body);
      const { username, password } = req.body;

      let currUser: any;
      MySQL.query(
        `SELECT fullname FROM users WHERE username = \"${username}\" AND users.password = MD5(\"${password}\");`,
        function (err, result, fields) {
          if (err) throw err;
          currUser = result;
          console.log(currUser);
          // Username/password do not exist
          if (currUser.length === 0)
            res.json({
              error:
                "Error: Login failed\nUsername does not exist, or password was typed incorrectly.",
            });
          // User exists but profile has not been set up yet
          else if (currUser[0].fullname == "" || currUser[0].fullname=="NAN")
            res.json({ success: "ProfileManage.html" });
          // User exists and profile has been set up
          else res.json({ success: "quote.html" });
        }
      );
    } catch (error) {
      if (error.isJoi === true)
        return next(new createError.BadRequest("Invalid Username/Password"));
      next(error);
    }
  },
};

